mapper.select:
   SELECT *
   FROM us_resume


       AND user_jg = #{user_jg}


         AND user_job LIKE CONCAT('%', #{user_job}, '%')


		 AND user_career >= #{user_career}


 		AND user_ws = #{user_ws}


 		AND user_wt = #{user_wt}


 		AND user_js = #{user_js}


       AND user_skill LIKE CONCAT('%', #{user_skill}, '%')


 		AND user_pay <= #{user_pay}
